const fs = require('fs');
const path = require('path');

console.log('Menyalin aset PDF.js...');

const sourceDir = path.resolve(__dirname, '../node_modules/pdfjs-dist/build');
const destDir = path.resolve(__dirname, '../public/assets/pdfjs/build');

// File yang akan disalin
const filesToCopy = [
    'pdf.js',
    'pdf.worker.js'
];

// Pastikan direktori tujuan ada
if (!fs.existsSync(destDir)) {
    fs.mkdirSync(destDir, { recursive: true });
    console.log(`Direktori dibuat: ${destDir}`);
}

filesToCopy.forEach(file => {
    const sourceFile = path.join(sourceDir, file);
    const destFile = path.join(destDir, file);
    fs.copyFileSync(sourceFile, destFile);
    console.log(`Disalin: ${file}`);
});

console.log('Selesai menyalin aset.');